import './ranking.css';

export default function Ranking(props) {
  //const [nickName, setNickName] = useState("") 
  const [ranking, setRanking] = useState (
    ["Player 1 - result 1"], 
    ["Player 2 - result 2"],
    ["Player 3 - result 3"],
  )

  useEffect(() => {
    localStorage.setItem('score', JSON.stringify(props.nickName));
  }, [ranking]);

  useEffect(() => {
    const ranking = JSON.parse(localStorage.getItem('score'));
    setRanking(ranking);    
  },[]);  
  
