      setRanking (`${props.nickName}  - RESULT  ${props.score} a`)
      score1st = props.score;
    } else if (props.score > score2nd && props.score > 5) {
      setRanking(`${props.nickName}  - RESULT  ${props.score} b`)
      score2nd = props.score;
    } else  if (props.score > score3rd && props.score > 5) {
      setRanking(`${props.nickName}  - RESULT  ${props.score} c`) 
      score3rd = props.score;    
    }

   // function showRanking () {
