import { useState, useEffect } from 'react';
import './ranking.css';

export default function Ranking(props) {
  //const [nickName, setNickName] = useState("") 
  let initialResult = [];
  initialResult[0] = "PLAYER 1 - RESULT ";
  initialResult[1] = "PLAYER 2 - RESULT ";
  initialResult[2] = "PLAYER 3 - RESULT ";

  const [ranking, setRanking] = useState (initialResult)

  useEffect(() => {
    localStorage.setItem('score', JSON.stringify(ranking));
  }, [ranking]);

  useEffect(() => {
    const ranking = JSON.parse(localStorage.getItem('score'));
    setRanking(ranking);    
  },[]);  
  
    let score1st = 0;
    let score2nd = 0;
    let score3rd = 0;

    if (props.score > score1st && props.score > 5) {
      setRanking (`${props.nickName}  - RESULT  ${props.score} a`)
      score1st = props.score;
    } else if (props.score > score2nd && props.score > 5) {
      setRanking(`${props.nickName}  - RESULT  ${props.score} b`)
      score2nd = props.score;
    } else  if (props.score > score3rd && props.score > 5) {
      setRanking(`${props.nickName}  - RESULT  ${props.score} c`) 
      score3rd = props.score;    
    }

   // function showRanking () {
   //   <div>{ranking}</div>
  //  }
    console.log(ranking)
    
    return (
      <div>
        <h2>My result is: </h2>
        {ranking}    
      </div>
    )
}
