import React, {useState} from 'react';
import './index.css';
import Boxes from './components/Boxes/Boxes.js';

export default function App() {  

  const [box, setBox] = useState(generateBoxes())

  function generateBoxes() {
    const boxes= [];
    for (let i = 0; i < 10; ++i) {
      boxes.push(Math.floor(Math.random() * 6) + 1);
    }
    return boxes
  }

  const boxElements = box.map(valueGenerated => <Boxes value = {valueGenerated} />)

  return (
    <main>      
      {boxElements}
      <button className='roll' onClick={generateBoxes} >ROLL</button>           
    </main>
  )
}
