import React, {useState, useEffect} from 'react';
import './index.css';
import Tenzi from './Images/tenzi.jpg';
import Boxes from './components/Boxes/Boxes.js';
import {nanoid} from 'nanoid';
import Timer from './components/Timer/Timer';
import Confetti from 'react-confetti';

export default function App() {
  const [box, setBox] = useState(generateBoxes())
  const [won, setWon] = useState(false)

  useEffect(() => {
     const allBox = box.every(item =>item.isPressed)
     const firstValue = box[0].value
     const allValue = box.every(item => item.value === firstValue)
     if (allBox && allValue) {
        setWon(true)
     }
  }, [box])

  function generateNewBox() {
    return {
      value: Math.floor(Math.random() * 6) + 1,
      isPressed: false,
      id: nanoid(),
    }
  }

  function generateBoxes() {
    const boxes= [];
    for (let i = 0; i < 10; ++i) {
      boxes.push(generateNewBox())
    }
    return boxes
  }

  function structureOfBox(id) {
    if (intervalId) {
      setBox(prevBox => prevBox.map(box => {
        return box.id === id ? {...box, isPressed: !box.isPressed} : box
      }))
    }
  }

  const boxElements = box.map(valueGenerated => (    
    <Boxes key={valueGenerated.id} value = {valueGenerated.value} isPressed={valueGenerated.isPressed} id={valueGenerated.id} structureOfBox={() => structureOfBox(valueGenerated.id)}/>
  ))

  function setGame() {
    if(!won) {
      if(intervalId) {
        setBox(prevBox => prevBox.map(box => {
          return box.isPressed ? 
          box :
          generateNewBox()
        }))
      }
    } else {
      setWon(false)
      setBox(generateBoxes())
      setSeconds(0);
      setMinutes(0);
      count = 0;
    }
  }

//-----------------Timer----------------------
  const [seconds, setSeconds] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [intervalId, setIntervalId] = useState(0);
  let count = 0;
  let difficulty = 0;

  const startStop = () => {
    if (intervalId) {
      count = seconds;
      clearInterval(intervalId);
      setIntervalId(0);
      return;
    }

    const newIntervalId = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds + 1);
        ++count; 
        if (count % 59 === 0) {
          setSeconds(0);
          setMinutes(prevMinutes => prevMinutes + 1);                    
        }
    }, 1000); 
    setIntervalId(newIntervalId);
  };

  return (
    <main>
        {
        difficulty !== 0
        ?
        <div>
          {won && startStop && <Confetti/>} 
          <div className='sec--title'>
            <img src={Tenzi} alt="Tenzi" className='title'/>        
          </div> 
          <Timer 
            seconds={seconds}
            minutes={minutes}
            intervalId={intervalId}
            startStop={startStop}        
          />
          <div className='container'> 
            {boxElements}
          </div>
          <button 
            className='roll' 
            onClick={setGame} 
            > 
              <strong>{won ? "New Game" : "ROLL"}</strong>
          </button> 
        </div>
        :
        <div>
          <h1>Sets the difficulty of the game</h1>
          <br/>          
          <button 
            className='ten'
            onClick={difficulty = 10}            
          > Choose the standard version          
          </button>
          <button 
            className='twenty'
            onClick={difficulty = 20}            
          > Choose the "double" version          
          </button>
          <button 
            className='startGame'
            onClick={setGame}            
          > Start Game         
          </button>
        </div>
      }
    </main>
  )
}
