import { useState, useEffect } from 'react';
import './ranking.css';

export default function Ranking(props) {  
  const [ranking, setRanking] = useState ([])  

  useEffect(() => {
    localStorage.setItem('score', JSON.stringify(props.nickName, props.score));
  }, [props.nickName, props.score]);

  useEffect(() => {
    JSON.parse(localStorage.getItem('score'));
    setRanking(`${props.nickName} - result ${props.score}`);  
    
  },[]);  
  
  
  console.log(props.nickName, props.score)

  return (
    <div>
       <div>
          <h1>Choose a nickname</h1> 
          <br/>
          <input
            className='name'
            type="text"
            placeholder="enter nickname here"                  
            onChange={props.handleChange}
            >        
          </input>       
        </div>
        <h2>My result is: </h2>
        <div>
          {ranking} 
        </div>
        <br/> <br/>
           
      </div>
    )
  }
  
