import { useState, useEffect } from 'react';
import './ranking.css';

export default function Ranking(props) {  
  const [ranking, setRanking] = useState ([props.nickName])  

  useEffect(() => {
    const updateRanking = window.localStorage.getItem('score'); 
    setRanking(JSON.parse(updateRanking))    
  },[]);  

  useEffect(() => {
    window.localStorage.setItem('score', JSON.stringify(ranking));
  }, [ranking]);
  
  console.log(ranking)

  return (
    <div>
       <div>
          <h1>Choose a nickname</h1> 
          <br/>
          <input
            className='name'
            type="text"
            placeholder="enter nickname here"                  
            onChange={props.handleChange}
            >        
          </input>       
        </div>
        <h2>My result is: </h2>
        <div>
          {ranking} 
        </div>
        <br/> <br/>
           
      </div>
    )
  }
  
