import './ranking.css';

export default function Ranking(props) {
    let ranking = [];
    let score1st = 0;
    let score2nd = 0;
    let score3rd = 0;
    ranking[0] = "PLAYER 1 - RESULT "
    ranking[1] = "PLAYER 2 - RESULT "
    ranking[2] = "PLAYER 3 - RESULT "

    if (props.score > score1st) {
      ranking[0] = `${props.nickName}  - RESULT  ${props.score}`;
      score1st = props.score;
    } else if (props.score > score2nd) {
      ranking[1] = `${props.nickName}  - RESULT  ${props.score}`;
      score2nd = props.score;
    } else  if (props.score > score3rd) {
      ranking[2] = `${props.nickName}  - RESULT  ${props.score}`; 
      score3rd = props.score;    
    }

    function showRanking () {
      <div>{ranking}</div>
    }
    console.log(ranking)
    
    return (
      <div>
        <h2>My result is: </h2>
        <button onClick={showRanking}>Show ranking</button>        
      </div>
    )
}
