import React, {useState, useEffect} from 'react';
import './index.css';
import Boxes from './components/Boxes/Boxes.js';
import {nanoid} from 'nanoid'

export default function App() {  

  const [box, setBox] = useState(generateBoxes())
  const [won, setWon] = useState(false)

  useEffect(() => {
     const allBox = box.every(item =>item.isPressed)
     const firstValue = box[0].value
     const allValue = box.every(item => item.value === firstValue)
     if (allBox && allValue) {
        setWon(true)
        console.log("You won!")
     }
  }, [box])

  function generateNewBox() { 
    return {   
      value: Math.floor(Math.random() * 6) + 1, 
      isPressed: false,
      id: nanoid()
    }
  }

  function generateBoxes() {
    const boxes= [];
    for (let i = 0; i < 10; ++i) {
      boxes.push(generateNewBox())    
    }
    return boxes
  }

  function structureOfBox(id) {
    setBox(prevBox => prevBox.map(box => {
      return box.id === id ? {...box, isPressed: !box.isPressed} : box
    }))
  }

  const boxElements = box.map(valueGenerated => (
    <Boxes key={valueGenerated.id} value = {valueGenerated.value} isPressed={valueGenerated.isPressed} id={valueGenerated.id} structureOfBox={() => structureOfBox(valueGenerated.id)}/>
  ))

  function setGame() {
    setBox(prevBox => prevBox.map(box => {
      return box.isPressed ? 
      box : 
      generateNewBox()
    }))
  }

  return (
    <main> 
      <div className='container'>
        {boxElements}
      </div>
      <button 
        className='roll' 
        onClick={setGame} 
        > 
          <strong>{won ? "New Game" : "ROLL"}</strong></button>           
    </main>
  )
}
