import React, {useState} from 'react';
import './index.css';
import Boxes from './components/Boxes/Boxes.js';

export default function App() {  

  const [box, setBox] = useState(generateBoxes())

  function generateBoxes() {
    const boxes= [];
    for (let i = 0; i < 10; ++i) {
      boxes.push({
        value: Math.floor(Math.random() * 6) + 1, 
        isPressed: false
     })    
    }
    return boxes
  }
  const boxElements = box.map(valueGenerated => <Boxes value = {valueGenerated.value} />)

  function setGame() {
    setBox(generateBoxes())
  }

  return (
    <main> 
      <div className='container'>
        {boxElements}
      </div>
      <button className='roll' onClick={setGame}><strong>ROLL</strong></button>           
    </main>
  )
}
