import { useState, useEffect } from 'react';
import './ranking.css';

export default function Ranking(props) {  
  /*let initialResult = [];
  initialResult[0] = "PLAYER 1 - RESULT ";
  initialResult[1] = "PLAYER 2 - RESULT ";
  initialResult[2] = "PLAYER 3 - RESULT ";*/

  const [ranking, setRanking] = useState ({
    first: "PLAYER 1 - result undefined",
    second: "PLAYER 2 - result undefined",
    third: "PLAYER 3 - result undefined",
    })

  useEffect(() => {
    localStorage.setItem('score', JSON.stringify(ranking));
  }, [ranking]);

  useEffect(() => {
    const ranking = JSON.parse(localStorage.getItem('score'));
    setRanking(ranking);    
  },[]);  
  
    let score1st = 0;
    let score2nd = 0;
    let score3rd = 0;

    if (props.score > score1st && props.score > 5) {
      ranking.first = `${props.nickName}  - RESULT  ${props.score}`
      score1st = props.score;
    } else if (props.score > score2nd && props.score > 5) {
      ranking.second = `${props.nickName}  - RESULT  ${props.score}`
      score2nd = props.score;
    } else  if (props.score > score3rd && props.score > 5) {
      ranking.third= `${props.nickName}  - RESULT  ${props.score}`
      score3rd = props.score;    
    }
    console.log(ranking)
    
    return (
      <div>
        <h2>My result is: </h2>
        {ranking}    
      </div>
    )
}
