import {useState, useEffect} from 'react';
import './index.css';
import Header from './components/Header/Header'
import Boxes from './components/Boxes/Boxes.js';
import {nanoid} from 'nanoid';
import Timer from './components/Timer/Timer';
import Confetti from 'react-confetti';
import SetDifficultyGame from './components/Difficulty/Difficulty';

export default function App() {
  const [box, setBox] = useState(generateInitialBoxes())
  const [won, setWon] = useState(false)
  let difficulty = 1;

  useEffect(() => {
     const allBox = box.every(item =>item.isPressed)
     const firstValue = box[0].value
     const allValue = box.every(item => item.value === firstValue)
     if (allBox && allValue) {
        setWon(true)
     }
  }, [box])

  function generateNewBox() {
    return {
      value: Math.floor(Math.random() * 6) + 1,
      isPressed: false,
      id: nanoid(),
    }
  }

  function generateInitialBoxes() {
    const boxes= [];    
    for (let i = 0; i < 10; ++i) {
      boxes.push(generateNewBox())
    }   
  return boxes    
  }

  function structureOfBox(id) {
    if (intervalId) {
      setBox(prevBox => prevBox.map(box => {
        return box.id === id ? {...box, isPressed: !box.isPressed} : box
      }))
    }
  }

  const boxElements = box.map(valueGenerated => (    
    <Boxes key={valueGenerated.id} value = {valueGenerated.value} isPressed={valueGenerated.isPressed} id={valueGenerated.id} structureOfBox={() => structureOfBox(valueGenerated.id)}/>
  ))

  function setGame() {
    if(!won) {
      if(intervalId) {
        setBox(prevBox => prevBox.map(box => {
          return box.isPressed ? 
          box :
          generateNewBox()
        }))
      }
    } else {
      setWon(false)
      setBox(generateInitialBoxes())
      setSeconds(0);
      setMinutes(0);
      count = 0;
    }
  }

//-----------------Timer----------------------
  const [seconds, setSeconds] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [intervalId, setIntervalId] = useState(0);
  let count = 0;  

  const startStop = () => {
    if (intervalId) {
      count = seconds;
      clearInterval(intervalId);
      setIntervalId(0);
      return;
    }

    const newIntervalId = setInterval(() => {
      setSeconds(prevSeconds => prevSeconds + 1);
        ++count; 
        if (count % 59 === 0) {
          setSeconds(0);
          setMinutes(prevMinutes => prevMinutes + 1);                    
        }
    }, 1000); 
    setIntervalId(newIntervalId);
  };  
//-----------------------------------------------
  return (
    <main>
        {
        difficulty !== 0
        ?
        <div>
          {won && startStop && <Confetti/>} 
          <Header /> 
          <Timer 
            seconds={seconds}
            minutes={minutes}
            intervalId={intervalId}
            startStop={startStop}        
          />
          <div className='container'> 
            {boxElements}
          </div>
          <button 
            className='roll' 
            onClick={setGame} 
            > 
              <strong>{won ? "New Game" : "ROLL"}</strong>
          </button> 
        </div>
        :
        <SetDifficultyGame  
          setDiffilculty={difficulty}
          numberOfBox={generateInitialBoxes}
        />
      }
    </main>
  )
}
